import React, { useState, useEffect } from 'react';
import { Card, Button, Table, message, Typography, Space, Tag } from 'antd';
import { ShareAltOutlined, CopyOutlined } from '@ant-design/icons';
import api from '../services/api';

const { Title, Text } = Typography;

const TestShares = () => {
  const [shares, setShares] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchShares();
  }, []);

  const fetchShares = async () => {
    try {
      setLoading(true);
      const response = await api.get('/api/files/shares/');
      console.log('Shares response:', response.data);
      setShares(response.data.results || response.data || []);
      message.success('分享列表加载成功');
    } catch (error) {
      console.error('Shares error:', error);
      message.error('加载分享失败: ' + (error.response?.data?.error || error.message));
    } finally {
      setLoading(false);
    }
  };

  const copyShareLink = (shareCode) => {
    const link = `http://localhost:3000/share/${shareCode}`;
    navigator.clipboard.writeText(link);
    message.success('分享链接已复制到剪贴板');
  };

  const columns = [
    {
      title: '文件名',
      dataIndex: ['file', 'original_name'],
      key: 'file_name',
      render: (text) => (
        <Space>
          <ShareAltOutlined />
          {text || '未知文件'}
        </Space>
      )
    },
    {
      title: '分享码',
      dataIndex: 'share_code',
      key: 'share_code',
      render: (code) => (
        <Space>
          <Text code>{code}</Text>
          <Button 
            size="small" 
            icon={<CopyOutlined />} 
            onClick={() => copyShareLink(code)}
          />
        </Space>
      )
    },
    {
      title: '下载次数',
      dataIndex: 'download_count',
      key: 'download_count',
      render: (count, record) => (
        <Text>
          {count} / {record.max_downloads || '无限制'}
        </Text>
      )
    },
    {
      title: '状态',
      dataIndex: 'is_active',
      key: 'is_active',
      render: (active) => (
        <Tag color={active ? 'green' : 'red'}>
          {active ? '活跃' : '已禁用'}
        </Tag>
      )
    },
    {
      title: '创建时间',
      dataIndex: 'created_at',
      key: 'created_at',
      render: (time) => time ? new Date(time).toLocaleString() : '-'
    }
  ];

  return (
    <div style={{ padding: '24px' }}>
      <Card>
        <Title level={3}>我的分享 (测试版)</Title>
        <Button onClick={fetchShares} loading={loading} style={{ marginBottom: 16 }}>
          刷新
        </Button>
        <Table
          columns={columns}
          dataSource={shares}
          rowKey="id"
          loading={loading}
          locale={{ emptyText: '暂无分享' }}
        />
      </Card>
    </div>
  );
};

export default TestShares;
