import React, { useState } from 'react';
import { 
  Card, 
  Button, 
  Table, 
  Modal, 
  Form, 
  Input, 
  Upload, 
  message,
  Space,
  Popconfirm,
  Tag,
  Typography,
  Row,
  Col,
  Tooltip
} from 'antd';
import { 
  UploadOutlined, 
  FolderAddOutlined, 
  DeleteOutlined,
  ShareAltOutlined,
  FileOutlined,
  FolderOutlined,
  ArrowLeftOutlined,
  CloudDownloadOutlined
} from '@ant-design/icons';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import api from '../services/api';

const { Title, Text } = Typography;
const { Dragger } = Upload;

const Files = () => {
  const { folderId } = useParams();
  const navigate = useNavigate();
  const queryClient = useQueryClient();
  const [uploadModalVisible, setUploadModalVisible] = useState(false);
  const [folderModalVisible, setFolderModalVisible] = useState(false);
  const [folderForm] = Form.useForm();

  const { data: folders = [] } = useQuery(
    ['folders', folderId],
    () => api.get('/api/files/folders/', { 
      params: folderId ? { parent_id: folderId } : {} 
    }).then(res => res.data)
  );

  const { data: filesData, isLoading } = useQuery(
    ['files', folderId],
    () => api.get('/api/files/', { 
      params: folderId ? { folder_id: folderId } : {} 
    }).then(res => res.data),
    { refetchOnWindowFocus: false }
  );

  const uploadMutation = useMutation(
    (formData) => {
      const data = new FormData();
      data.append('file', formData.file.file);
      if (folderId) {
        data.append('folder_id', folderId);
      }
      // 移除手动设置的Content-Type，让浏览器自动设置（包含boundary）
      return api.post('/api/files/upload/', data);
    },
    {
      onSuccess: () => {
        message.success('文件上传成功！');
        setUploadModalVisible(false);
        queryClient.invalidateQueries('files');
        queryClient.invalidateQueries('storage-info');
      },
      onError: (error) => {
        const errorMsg = error.response?.data?.error || '上传失败';
        message.error(errorMsg);
      }
    }
  );

  const createFolderMutation = useMutation(
    (data) => api.post('/api/files/folders/create/', {
      ...data,
      parent_id: folderId
    }),
    {
      onSuccess: () => {
        message.success('文件夹创建成功！');
        setFolderModalVisible(false);
        folderForm.resetFields();
        queryClient.invalidateQueries('folders');
      },
      onError: (error) => {
        const errorMsg = error.response?.data?.error || '创建失败';
        message.error(errorMsg);
      }
    }
  );

  const deleteFileMutation = useMutation(
    (fileId) => api.delete(`/api/files/${fileId}/delete/`),
    {
      onSuccess: () => {
        message.success('文件删除成功！');
        queryClient.invalidateQueries('files');
        queryClient.invalidateQueries('storage-info');
      },
      onError: (error) => {
        const errorMsg = error.response?.data?.error || '删除失败';
        message.error(errorMsg);
      }
    }
  );

  const deleteFolderMutation = useMutation(
    (folderId) => api.delete(`/api/files/folders/${folderId}/delete/`),
    {
      onSuccess: () => {
        message.success('文件夹删除成功！');
        queryClient.invalidateQueries('folders');
      },
      onError: (error) => {
        const errorMsg = error.response?.data?.error || '删除失败';
        message.error(errorMsg);
      }
    }
  );

  const uploadProps = {
    name: 'file',
    multiple: false,
    customRequest: ({ file, onSuccess, onError }) => {
      uploadMutation.mutate({ file }, {
        onSuccess: () => onSuccess(),
        onError: (error) => onError(error)
      });
    },
    showUploadList: false,
  };

  const folderColumns = [
    {
