          </Tooltip>
        </Space>
      ),
    },
    {
      title: '大小',
      dataIndex: 'size_display',
      key: 'size_display',
    },
    {
      title: '类型',
      dataIndex: 'file_type',
      key: 'file_type',
      render: (type) => (
        <Tag color="blue">{type || '未知'}</Tag>
      ),
    },
    {
      title: '上传时间',
      dataIndex: 'created_at',
      key: 'created_at',
      render: (text) => new Date(text).toLocaleString(),
    },
    {
      title: '操作',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button 
            type="text" 
            icon={<CloudDownloadOutlined />}
            size="small"
            onClick={() => window.open(record.swift_url)}
            disabled={!record.swift_url}
          />
          <Button 
            type="text" 
            icon={<ShareAltOutlined />}
            size="small"
            onClick={() => handleShareFile(record.id)}
          />
          <Popconfirm
            title="确定删除这个文件吗？"
            onConfirm={() => deleteFileMutation.mutate(record.id)}
            okText="确定"
            cancelText="取消"
          >
            <Button 
              type="text" 
              danger 
              icon={<DeleteOutlined />}
              size="small"
            />
          </Popconfirm>
        </Space>
      ),
    },
  ];

  const getFileIconColor = (extension) => {
    const colorMap = {
      '.pdf': '#ff6b6b',
      '.doc': '#4facfe',
      '.xls': '#43e97b',
      '.jpg': '#fa709a',
      '.png': '#fee140',
      '.mp4': '#30cfd0',
      '.mp3': '#a8edea',
      '.zip': '#ff9a9e',
    };
    return colorMap[extension] || '#667eea';
  };

  const handleShareFile = (fileId) => {
    // 这里可以添加分享逻辑
    message.info('分享功能开发中...');
  };

  return (
    <div>
      <div style={{ marginBottom: '24px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Space>
          {folderId && (
            <Button 
              icon={<ArrowLeftOutlined />} 
              onClick={() => navigate('/files')}
            >
              返回上级
            </Button>
          )}
          <Title level={2} style={{ color: '#333', margin: 0 }}>
            我的文件
          </Title>
        </Space>

        <Space>
          <Button 
            type="primary" 
            icon={<UploadOutlined />}
            onClick={() => setUploadModalVisible(true)}
            className="cel-button"
          >
            上传文件
          </Button>
          <Button 
            icon={<FolderAddOutlined />}
            onClick={() => setFolderModalVisible(true)}
            className="cel-button"
          >
            新建文件夹
