              dataSource={folders}
              rowKey="id"
              pagination={false}
              size="small"
            />
          </Card>
        </Col>

        <Col span={24}>
          <Card className="cel-card" title="文件">
            <Table
              columns={fileColumns}
              dataSource={filesData?.results || filesData || []}
              rowKey="id"
              loading={isLoading}
              pagination={{
                total: filesData?.count,
                pageSize: 20,
                showSizeChanger: false,
                showQuickJumper: true,
              }}
              size="small"
            />
          </Card>
        </Col>
      </Row>

      {/* 上传文件模态框 */}
      <Modal
        title="上传文件"
