import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Card, Typography, message, Alert, Spin } from 'antd';
import { UserOutlined, LockOutlined, CloudOutlined, ReloadOutlined } from '@ant-design/icons';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';

const { Title, Text } = Typography;

const Login = () => {
  const [loading, setLoading] = useState(false);
  const [apiStatus, setApiStatus] = useState({ checking: true, online: false });
  const { login } = useAuth();
  const navigate = useNavigate();

  // 检查API连接状态
  useEffect(() => {
    const checkApiStatus = async () => {
      try {
        const response = await api.get('/api/auth/', { timeout: 5000 });
        setApiStatus({ checking: false, online: true });
      } catch (error) {
        setApiStatus({ checking: false, online: false });
        console.warn('API连接检查失败:', error.message);
      }
    };

    checkApiStatus();
  }, []);

  const onFinish = async (values) => {
    setLoading(true);
    try {
      const result = await login(values);
      if (result.success) {
        message.success('登录成功！正在跳转...');
        setTimeout(() => navigate('/dashboard'), 1000);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleRetryApi = () => {
    setApiStatus({ checking: true, online: false });
    setTimeout(() => {
      checkApiStatus();
    }, 1000);
  };

  const checkApiStatus = async () => {
    try {
      await api.get('/api/auth/', { timeout: 5000 });
      setApiStatus({ checking: false, online: true });
    } catch (error) {
      setApiStatus({ checking: false, online: false });
    }
  };

  return (
    <div className="cel-gradient-bg" style={{
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '20px',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    }}>
      <div className="cel-float">
        {/* API状态提示 */}
        {!apiStatus.checking && !apiStatus.online && (
          <Alert
            message="后端服务连接失败"
            description="无法连接到后端API服务，请确保后端服务正在运行。"
            type="error"
            showIcon
            closable={false}
            action={
              <Button size="small" onClick={handleRetryApi}>
                <ReloadOutlined /> 重试
              </Button>
            }
            style={{ marginBottom: '20px', borderRadius: '10px' }}
          />
        )}

        {/* API检查中提示 */}
        {apiStatus.checking && (
          <Alert
            message="正在检查服务状态..."
            type="info"
            showIcon
            closable={false}
            style={{ marginBottom: '20px', borderRadius: '10px' }}
          />
        )}

        <Card
          style={{
            width: '100%',
            maxWidth: '400px',
            padding: '20px',
            background: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(255, 255, 255, 0.2)',
            borderRadius: '20px',
            boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)'
          }}
        >
          <div style={{ textAlign: 'center', marginBottom: '32px' }}>
            <div style={{
              width: '80px',
              height: '80px',
              background: 'linear-gradient(145deg, #ff6b6b, #ff8e53)',
              borderRadius: '20px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '36px',
              fontWeight: 'bold',
              margin: '0 auto 16px'
            }}>
              <CloudOutlined />
            </div>
            <Title level={2} style={{ color: '#333', margin: 0 }}>
              云盘登录
            </Title>
            <Text type="secondary">
              欢迎回到您的云存储空间
            </Text>
          </div>

          <Form
            name="login"
            onFinish={onFinish}
            layout="vertical"
            size="large"
          >
            <Form.Item
              name="username"
              rules={[{ required: true, message: '请输入用户名' }]}
            >
              <Input
                prefix={<UserOutlined />}
                placeholder="用户名"
                className="cel-input"
              />
            </Form.Item>

            <Form.Item
              name="password"
              rules={[{ required: true, message: '请输入密码' }]}
            >
              <Input.Password
                prefix={<LockOutlined />}
                placeholder="密码"
                className="cel-input"
              />
            </Form.Item>

            <Form.Item>
              <Button
                type="primary"
                htmlType="submit"
                loading={loading}
                block
                className="cel-button"
                style={{
                  height: '48px',
                  fontSize: '16px',
                  fontWeight: 'bold'
                }}
              >
                登录
              </Button>
            </Form.Item>

            <div style={{ textAlign: 'center' }}>
              <Text type="secondary">
                还没有账号？{' '}
                <Link to="/register" style={{ color: '#667eea', fontWeight: 'bold' }}>
                  立即注册
                </Link>
              </Text>
            </div>
          </Form>
        </Card>
      </div>
    </div>
  );
};

export default Login;
